mapper.select:
SELECT COUNT(*) FROM pms_product_category WHERE delete_time IS NULL

    AND parent_id = #{parentId}


    AND name = #{name}


    AND level = #{level}

SELECT
    id, parent_id, name, level, product_count, product_unit, nav_status, show_status, sort,
    icon, keywords, description, create_time, update_time, delete_time
FROM
    pms_product_category WHERE delete_time IS NULL

    AND parent_id = #{parentId}


    AND name = #{name}


    AND level = #{level}

LIMIT #{pageSize} OFFSET #{offset}
